mapper.select:
SELECT * FROM ECO_PRODUCT WHERE 1=1
AND CODE = #{code}
AND ITEM_NAME LIKE '%' #{keyword} '%'

SELECT CODE, MONTH, SUM(TOTALPAY) AS TOTALPAY
FROM (
SELECT P.CODE, MONTH, VALUE*(PRICE*(100-S_RATE)/100) AS TOTALPAY
FROM ECO_PRODUCT P
INNER JOIN ECO_PRODUCT_CHART C ON P.ITEM_NO = C.ITEM_NO
WHERE P.CODE =#{code}
) AS subquery
GROUP BY CODE, MONTH
ORDER BY MONTH ASC;

SELECT * FROM ( SELECT U.USERID, U.NAME, U.BIRTH, U.GENDER, U.NICKNAME, U.PHONE1, U.PHONE2, U.PHONE3, U.LOGINCNT, U.USER_GRADE, U.USERTYPE, U.EMAIL, @row_number:=@row_number + 1 AS PAGE FROM (SELECT @row_number:=0) AS t, ECO_USER U WHERE 1=1
AND USERID LIKE CONCAT('%', #{keyword}, '%')
AND NAME LIKE CONCAT('%', #{keyword}, '%')
) AS subquery WHERE PAGE BETWEEN #{startNum} AND #{lastNum}
