mapper.select:
SELECT U.*, CONCAT(PHONE1, PHONE2, PHONE3) AS PHONE
FROM ECO_USER U
WHERE USERID = #{userId}

SELECT USERID
FROM ECO_USER
WHERE CONCAT(PHONE1, PHONE2, PHONE3) = #{phoneNumber}

mapper.update:
UPDATE ECO_USER
SET
	USERPW = #{userPw}
WHERE USERID = #{userId}

UPDATE ECO_USER
SET
	USERPW = #{userPw}, NAME = #{name}, NICKNAME = #{nickName}, GENDER = #{gender},
	PHONE1 = #{phone1}, PHONE2 = #{phone2}, PHONE3 = #{phone3}, EMAIL = #{email}, BIRTH = #{birth}
WHERE USERID = #{userId}

UPDATE ECO_ADDR
SET ISDEFAULT = 'Y'
WHERE ADDRNO = #{addrNo}

mapper.delete:
DELETE FROM ECO_USER_DELETE WHERE USERID = #{userId}
UPDATE ECO_USER_DELETE SET DELETE_DATE = DATE_ADD(NOW(), INTERVAL 1 MONTH)
WHERE USERID = #{userId}
